import React from 'react';
import Tile from './Tile';
import { useDispatch, useSelector } from 'react-redux';
import { selectTile } from '../redux/actions';

const Board = () => {
    const tiles = useSelector(state => state.tiles); // Получаем список плиток из состояния
    const dispatch = useDispatch(); // Получаем диспетчер

    return (
        <div className="board">
            {tiles.map(tile => (
                <Tile
                    key={tile.id} // Используйте уникальный идентификатор
                    number={tile.number} // Передаем номер плитки
                    isOpen={tile.isOpen} // Статус плитки
                    onClick={() => dispatch(selectTile(tile.id))} // Диспетчер для выбора плитки
                />
            ))}
        </div>
    );
};

export default Board;

